import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProblemPage = () => {
  const { id } = useParams();
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchProblem = async () => {
      const res = await axios.get(`http://localhost:5000/api/problems`);
      setProblem(res.data.find(p => p._id === id));
    };
    fetchProblem();
  }, [id]);

  const handleSubmit = async () => {
  try {
    const token = localStorage.getItem('token');
    const res = await axios.post(
      `http://localhost:5000/api/submit/${id}`,
      { code, language: 'python3' },
      {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      }
    );
    setResults(res.data.results);
  } catch (err) {
    alert('Submission failed');
    console.error('Submission error:', err);
  }
};

  if (!problem) return <div>Loading...</div>;

  return (
    <div>
      <h2>{problem.title}</h2>
      <p>{problem.description}</p>
      <textarea rows="10" cols="60" value={code} onChange={e => setCode(e.target.value)} />
      <br />
      <button onClick={handleSubmit}>Submit</button>
      <div>
        <h3>Results:</h3>
        <ul>
          {results.map((r, idx) => (
            <li key={idx} style={{ color: r.pass ? 'green' : 'red' }}>
              Input: {r.input}, Expected: {r.expected}, Got: {r.received.trim()}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ProblemPage;
